import logo from "../img/logo.png";
import Underline from "./Underline";
import { useState } from "react";
import { Link } from "react-router-dom";

function Header() {
  const [hoverTarget, setHoverTarget] = useState(null);

  const hoverHandler = (e) => {
    setHoverTarget(e.target);
  };

  return (
    <div className="Header">
      <div className="logo_div">
        <Link href="/">
          <img className="logo_img" src={logo}></img>
        </Link>
      </div>
      <ul>
        <li>
          <Link href="/introduction" onMouseEnter={hoverHandler}>
            회사소개
          </Link>
        </li>
        <li>
          <Link to="/programs">프로그램</Link>
        </li>
        <li>
          <Link to="/lesson">레슨문의</Link>
        </li>
        <li>
          <Link href="/branch">지사개설</Link>
        </li>
        <li>
          <Link href="/teacher">교사지원</Link>
        </li>
        <li>
          <Link href="/community">커뮤니티</Link>
        </li>
      </ul>
      gvhmbvmb
      <Underline hoverTafgrget={hoverTarget} />
    </div>
  );
}

export default Header;
