function Home() {
  const settings = {
    dots: false,
    fade: true,
    infinite: true,
    //speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    //
    autoplay: true,
    autoplaySpeed: 500,
  };
  return (
    <div className="slicky_container">
      <Slider {...settings}>
        <div>
          <img src={violin} className="slickyimg" />
        </div>
        <div>
          <img src={piano} className="slickyimg" />
        </div>
        <div>
          <img src={flute} className="slickyimg" />
        </div>
      </Slider>
    </div>
  );
}

export default Home;

